    val listData: ArrayList<Panti_Asuhan>
        get() {
            val list = ArrayList<Panti_Asuhan>()
            for (position in pantisName.indices) {
                val pantis = Panti_Asuhan()
                pantis.name = pantisName[position]
                pantis.address = pantisAddress[position]
                pantis.photo = imagePantis[position]
                pantis.detail = pantisDetail[position]

                list.add(pantis)
            }

            return list
        }

